import React, { useRef, useState } from 'react'
import './TodoList.css'
export const TodoList = () => {
    const [todo, setTodo] = useState();
    const myReff = useRef(null);
    // Add a todo
    const handleAddTodo = () => {
        const myvalue = myReff.current.value;
        setTodo([...todo,{value :myvalue ,completed:true}]);
        myReff.current.value='';
        console.log(todo);

    }
    return (
        <div className='todoList-container'>
            <h1 className='todo-title'>To Do List</h1>
            <ul className="todo-list">

            </ul>
            <input ref={myReff} className='add-input' type="text" placeholder=' Enter a value...' />
            <button className='add-btn' onClick={()=>handleAddTodo()}>Add Todo</button>

        </div>
    )
}
